var utf8StringBytes = require('./');
var assert = require('assert');

describe('utf8StringBytes', function() {
  describe('#stringToUtf8ByteArray()', function() {
    it('should return "[79, 108, 195, 161]" when the value is "Ol치"', function() {
      assert.deepEqual([79, 108, 195, 161], utf8StringBytes.stringToUtf8ByteArray("Ol치"));
    });
  });
  describe('#utf8ByteArrayToString()', function() {
    it('should return "Ol치" when the value is "[79, 108, 195, 161]"', function() {
      assert.equal("Ol치", utf8StringBytes.utf8ByteArrayToString([79, 108, 195, 161]));
    });
  });
});